mapper.update:
update article_kind
kind_name = #{kindName}, status = #{status}, update_time = #{updateTime}, create_time = #{createTime}, admin_id = #{adminId}, parent_kind_id = #{parentKindId},
where article_kind_id = #{articleKindId}

mapper.select:
SELECT * FROM article_kind

	kind_name LIKE CONCAT('%', #{kindName}, '%')
	AND status = #{status}
	AND DATE_FORMAT(create_time, '%Y-%m-%d') >= DATE_FORMAT(#{beginTime}, '%Y-%m-%d')
	AND DATE_FORMAT(#{endTime}, '%Y-%m-%d') >= DATE_FORMAT(create_time, '%Y-%m-%d')

LIMIT #{startRow}, #{onePageSize}
