mapper.update:
update article
article_title = #{articleTitle}, article_content = #{articleContent}, article_url = #{articleUrl}, article_from = #{articleFrom}, article_author = #{articleAuthor}, status = #{status}, update_time = #{updateTime}, create_time = #{createTime}, is_original = #{isOriginal}, is_valid = #{isValid},
where article_id = #{articleId}

mapper.select:
		select 
			article_id,article_title,article_content,article_from,article_url,article_author, update_time, create_time
        from article where status=1  order by article_id desc limit #{startRow},#{onePageSize}

select a.article_id,a.article_title,a.article_content,a.article_from,a.article_url,a.article_author,a.update_time,a.create_time, ak.kind_pinyin from article a left join article_kind_rel akr on a.article_id = akr.article_id left join article_kind ak on akr.article_kind_id = ak.article_kind_id where a.status =1 and akr.status=1 and ak.status = 1
and ak.kind_pinyin =#{kindPinyin}
order by a.article_id desc limit #{startRow},#{onePageSize}

select a.article_id,a.article_title,a.article_content,a.article_from,a.article_url,a.article_author,a.update_time,a.create_time from article a where a.status =1
and a.article_title like CONCAT('%',#{title},'%')
order by a.article_id desc limit #{startRow},#{onePageSize}

select count(*) from article

	article_title like concat('%', #{articleTitle}, '%')
	AND status = #{status}
	AND DATE_FORMAT(create_time, '%Y-%m-%d') >= DATE_FORMAT(#{beginTime}, '%Y-%m-%d')
	AND DATE_FORMAT(#{endTime}, '%Y-%m-%d') >= DATE_FORMAT(create_time, '%Y-%m-%d')
	AND is_original = #{isOriginal}

select * from article

	article_title like concat('%', #{articleTitle}, '%')
	AND status = #{status}
	AND DATE_FORMAT(create_time, '%Y-%m-%d') >= DATE_FORMAT(#{beginTime}, '%Y-%m-%d')
	AND DATE_FORMAT(#{endTime}, '%Y-%m-%d') >= DATE_FORMAT(create_time, '%Y-%m-%d')
	AND is_original = #{isOriginal}
	

order by create_time desc
LIMIT #{startRow}, #{onePageSize}

select a.article_id,a.article_title,a.article_content,a.article_from,a.article_url,a.article_author,a.update_time,a.create_time, ak.kind_pinyin from article a left join article_kind_rel akr on a.article_id = akr.article_id left join article_kind ak on akr.article_kind_id = ak.article_kind_id where a.status =1 and akr.status=1 and ak.status = 1 and a.article_id!=#{articleId}
and ak.kind_pinyin =#{pinyin}
order by rand() limit 0,2

select a.article_id,a.article_title,a.article_content,a.article_from,a.article_url,a.article_author,a.update_time,a.create_time,a.status from article_copy a
article_title like concat('%', #{articleTitle}, '%') AND DATE_FORMAT(create_time, '%Y-%m-%d') >= DATE_FORMAT(#{beginTime}, '%Y-%m-%d') AND DATE_FORMAT(#{endTime}, '%Y-%m-%d') >= DATE_FORMAT(create_time, '%Y-%m-%d') AND is_valid = 1
order by create_time desc LIMIT #{startRow}, #{onePageSize}
