-- ----------------------------
-- Table structure for `attachment`
-- ----------------------------
DROP TABLE IF EXISTS `attachment`;
CREATE TABLE `attachment` (
  `attachment_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `article_id` int(11) DEFAULT NULL COMMENT '对应文章ID，与其一对一关系',
  `attachment_name` varchar(100) DEFAULT NULL COMMENT '文件名',
  `attachment_type` varchar(10) DEFAULT NULL COMMENT '后缀',
  `create_date` datetime NOT NULL COMMENT '创建时间',
  `attachmen_sts` char(1) DEFAULT NULL COMMENT '是否有效，1有效，0无效',
  `large` varchar(200) DEFAULT NULL COMMENT '最大尺寸图片地址，对应首页banner',
  `midSize` varchar(200) DEFAULT NULL COMMENT '中型图片地址，对应文章开头图片',
  `small` varchar(200) DEFAULT NULL COMMENT '最小尺寸图片地址，对应详情页面推荐小图',
  `attachment_old_name` varchar(100) DEFAULT NULL COMMENT '上传文件的旧名字',
  PRIMARY KEY (`attachment_id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

